import axios from 'axios';
import { BASE_URL } from './apiConfig';

// Function to add a new patient
export const addPatient = (name, age, condition) => {
  return axios.post(`${BASE_URL}/patients`, { name, age, condition });
};

// Function to get all patients
export const getAllPatients = () => {
  return axios.get(`${BASE_URL}/patients`);
};

// Function to get patient details by ID
export const getPatientById = (id) => {
    return axios.get(`${BASE_URL}/patients/${id}`);
  };

  // Function to get notes by patient ID
  export const getNotesByPatientId = (id) => {
    return axios.get(`${BASE_URL}/patients/${id}/notes`);
  };

  // Function to add a note to a patient by ID
  export const addNoteToPatient = (id, note) => {
    return axios.post(`${BASE_URL}/patients/${id}/notes`, { note });
  };
