// PatientListPage.js
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { getAllPatients } from '../api/patientApi'; // Import patient API
import '../index.css';

function PatientListPage() {
  const [patients, setPatients] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    // בקשה לנתונים של המטופלים מהשרת
    getAllPatients()
      .then(response => {
        setPatients(response.data);
      })
      .catch(error => {
        console.error("Error fetching patients", error);
      });
  }, []);

  return (
    <div className="patient-list-container">
      <h2>רשימת מטופלים</h2>
      <button className="add-patient-button" onClick={() => navigate('/patients/add')}>הוסף מטופל חדש</button>
      <ul className="patient-list">
        {patients.map(patient => (
          <li key={patient.id} className="patient-item" onClick={() => navigate(`/patients/${patient.id}`)}>
            {`שם: ${patient.name}, ID: ${patient.id}`}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default PatientListPage;
